import React,{useState} from "react";
import AddIcon from '@mui/icons-material/Add';
import Fab from '@mui/material/Fab';
import Zoom from '@mui/material/Zoom';

function CreateArea(props){
    const [note,setNote]=useState({
        title:"",
        content:""
    });
    const [bool,setBool]=useState(false);
    function handleChange(event){
        const {name,value}=event.target;
        setNote((prevValue)=>{
            if(name==="title"){
                return {title:value,content:prevValue.content};
            }
            else {
                return {title:prevValue.title,content:value};
            }
        })
    }
    function handleClick(){
        setBool(true);
    }
    return(
        <div>
          <Zoom in={bool}>
            <form className="create-note">
                {bool && <input onChange={handleChange} name="title" placeholder="Title" value={note.title}/>}
                <textarea onClick={handleClick} onChange={handleChange} name="content" placeholder="Take a note..." {...bool?rows="1":rows="3"} value={note.content}/>
                <Fab onClick={(event)=>{props.onClick(note);event.preventDefault(); setNote({title:"", content:""})}}><AddIcon/></Fab>
            </form>
            </Zoom>
        </div>
    );
}

export default CreateArea;
